import React, {useState} from "react";
import { useCookies } from "react-cookie";


// Stateless Functional Component

const NavBar = ({ totalCounters, totalSum}) => {

  const [ttCnt, setTtCnt] = useState('');
  const [ts, setTs] = useState('');
  const [cookies, setCookie] = useCookies(['user']);
  const handle = () => {
    setCookie('TotalCounters', totalCounters, {path: '/'});
    setCookie('TotalSum', totalSum, {path: '/'});
  }
  return (
    <nav className="navbar navbar-light bg-light">
      <div className="navbar-brand">
        <i className="fa fa-shopping-cart fa-lg m-2" aria-hidden="true" />
        <span className="badge badge-pill badge-info m-2" style={{ width: 50 }}>
          {cookies.TotalSum || totalSum}
        </span>/
        <span className="badge badge-pill badge-info m-2" style={{ width: 50 }}>
          {cookies.TotalCounters || totalCounters}
        </span>
        Items
      </div>
    </nav>
  );
};

export default NavBar;
